mapper.select:
SELECT tableone.*,user_name FROM
(SELECT user_code,user_project AS project_name,flow_year,flow_month,flow_date,
IFNULL(flow_money_out,0) AS flow_money_out,IFNULL(flow_money_in,0) AS flow_money_in,flow_type,flow_process_id
FROM fin_money_flow WHERE  1=1
 and  user_code=#{arg0}
 and  user_project=#{arg1}
 and  flow_year=#{arg2}
 and  flow_month=#{arg3}
 and  flow_type=#{arg4}
LIMIT #{arg5},#{arg6}) tableone
LEFT JOIN sys_user su ON su.user_id=tableone.user_code

SELECT COUNT(flow_id)
FROM fin_money_flow WHERE  1=1
 and  user_code=#{arg0}
 and  user_project=#{arg1}
 and  flow_year=#{arg2}
 and  flow_month=#{arg3}
 and  flow_type=#{arg4}

SELECT tableone.*,user_name FROM
(SELECT user_code,user_project AS project_name,flow_year,flow_month,flow_date,IFNULL(flow_money_out,0) AS flow_money_out,
IFNULL(flow_money_in,0) AS flow_money_in,flow_type,flow_process_id
FROM fin_pro_money_flow WHERE  1=1
 and  user_code=#{arg0}
 and  user_project=#{arg1}
 and  flow_year=#{arg2}
 and  flow_month=#{arg3}
 and  flow_type=#{arg4}
LIMIT #{arg5},#{arg6}) tableone
LEFT JOIN sys_user su ON su.user_id=tableone.user_code

SELECT COUNT(flow_id)
FROM fin_pro_money_flow WHERE  1=1
 and  user_code=#{arg0}
 and  user_project=#{arg1}
 and  flow_year=#{arg2}
 and  flow_month=#{arg3}
 and  flow_type=#{arg4}

SELECT user_project AS project_name, '至今' AS flow_year, flow_year, flow_month, '全年' AS flow_month, flow_type, '无' AS flow_type, IFNULL(SUM(flow_money_out),0) AS flow_money_out, IFNULL(SUM(flow_money_in),0) AS flow_money_in FROM fin_money_flow fin_pro_money_flow fin_money_flow WHERE 1=1 and user_code=#{arg0} and user_project=#{arg1} and flow_year=#{arg2} and flow_month=#{arg3} and flow_type=#{arg4} GROUP BY
user_project, flow_year DESC,flow_month DESC, flow_type
LIMIT #{arg6},#{arg7}

SELECT COUNT(*) FROM
(SELECT COUNT(*)
FROM
fin_money_flow
fin_pro_money_flow
fin_money_flow
WHERE 1=1and user_code=#{arg0}
and user_project=#{arg1}
and flow_year=#{arg2}
and flow_month=#{arg3}
and flow_type=#{arg4}
GROUP BY

  user_project,
  flow_year,flow_month,
  flow_type
) table_one
