mapper.select:
select * from sys_user where 1=1

  and  user_id = #{userId,jdbcType=VARCHAR}


  and  user_code = #{userCode,jdbcType=VARCHAR}


  and  user_password = #{userPassword,jdbcType=VARCHAR}


  and  salt = #{salt,jdbcType=VARCHAR}


  and  status = #{status,jdbcType=INTEGER}


  and  user_name = #{userName,jdbcType=VARCHAR}


  and  email = #{email,jdbcType=VARCHAR}


  and create_time = #{createTime,jdbcType=VARCHAR}


  and  error_count = #{errorCount,jdbcType=VARCHAR}


  and  department_id = #{departmentId,jdbcType=VARCHAR}


  and  images = #{images,jdbcType=VARCHAR}


  and  phone = #{phone,jdbcType=VARCHAR}


  and  ts = #{ts,jdbcType=VARCHAR}


  and  ip = #{ip,jdbcType=VARCHAR}


  and  sex = #{sex,jdbcType=VARCHAR}


  and  age = #{age,jdbcType=VARCHAR}


  and  last_time = #{last_time,jdbcType=VARCHAR}


  and  login_time = #{login_time,jdbcType=VARCHAR}


 and id_num = #{idNum,jdbcType=VARCHAR}


  and bank_card_num = #{bankCardNum,jdbcType=VARCHAR}


  and temp_var1 = #{tempVar1,jdbcType=VARCHAR}


  and  temp_var2 = #{tempVar2,jdbcType=VARCHAR}


  and temp_var3 = #{tempVar3,jdbcType=VARCHAR}


  and temp_int1 = #{tempInt1,jdbcType=INTEGER}


  and temp_int2 = #{tempInt2,jdbcType=INTEGER}


  and  temp_int3 = #{tempInt3,jdbcType=INTEGER}

SELECT tableone.user_id, tableone.user_code, tableone.status, tableone.user_name, tableone.email, tableone.create_time, tableone.error_count, tableone.images, tableone.phone, tableone.ts, tableone.ip, tableone.sex, tableone.last_time, tableone.login_time, tableone.bank_card_num, tableone.id_num, d.department_id AS departmentId, d.department_name AS departmentName, d.department_code AS departmentCode, d. STATUS AS de_status, d.ts AS ts_b, p.project_id AS project_id, p.project_name AS project_name, p.project_introduction AS project_introduction, p.project_address AS project_address, p. STATUS AS pro_status, p.start_time AS start_time, p.end_time AS end_time, p.ts AS pro_ts FROM ( SELECT * FROM ( SELECT * FROM sys_user limit #{page} , #{limit} ) u LEFT JOIN ( SELECT user_id AS u_id, department_id AS d_id FROM sys_user_department ) ud ON u.user_id = ud.u_id ) tableone LEFT JOIN sys_department d ON tableone.d_id = d.department_id LEFT JOIN sys_project p ON tableone.temp_int1 = p.project_id
where 1 =1
and tableone.user_code = #{user.userCode,jdbcType=VARCHAR}
and tableone.status = #{user.status,jdbcType=INTEGER}
and tableone.user_name = #{user.userName,jdbcType=VARCHAR}
and tableone.email = #{user.email,jdbcType=VARCHAR}
and tableone.phone = #{user.phone,jdbcType=VARCHAR}
and tableone.sex = #{user.sex,jdbcType=VARCHAR}
and tableone.temp_var1 = #{user.tempVar1,jdbcType=VARCHAR}
and tableone.temp_var2 = #{user.tempVar2,jdbcType=VARCHAR}
and d.department_id = #{user.tempVar3,jdbcType=VARCHAR}
and p.project_id = #{user.tempInt1,jdbcType=INTEGER}
and tableone.temp_int2 = #{user.tempInt2,jdbcType=INTEGER}
and tableone.temp_int3 = #{user.tempInt3,jdbcType=INTEGER}
ORDER by tableone.user_id DESC

    SELECT
    count(DISTINCT tableone.user_id)

    FROM
    (
    SELECT
    *
    FROM
    (
    SELECT
    *
    FROM
    sys_user
     ) u
    LEFT JOIN (
    SELECT
    user_id AS u_id,
    department_id AS d_id
    FROM
    sys_user_department
    ) ud ON u.user_id = ud.u_id
    ) tableone
    LEFT JOIN sys_department d ON tableone.d_id = d.department_id
    LEFT JOIN sys_project p ON tableone.temp_int1 = p.project_id
    where 1 =1

    
      and  tableone.user_code = #{user.userCode,jdbcType=VARCHAR}
    
    
      and  tableone.status = #{user.status,jdbcType=INTEGER}
    
    
      and  tableone.user_name = #{user.userName,jdbcType=VARCHAR}
    
    
      and  tableone.email = #{user.email,jdbcType=VARCHAR}
    
    
      and  tableone.phone = #{user.phone,jdbcType=VARCHAR}
    
    
      and  tableone.sex = #{user.sex,jdbcType=VARCHAR}
    
    
      and tableone.temp_var1 = #{user.tempVar1,jdbcType=VARCHAR}
    
    
      and  tableone.temp_var2 = #{user.tempVar2,jdbcType=VARCHAR}
    
    
    
      and d.department_id = #{user.tempVar3,jdbcType=VARCHAR}
    
    
    
      and p.project_id = #{user.tempInt1,jdbcType=INTEGER}
    
    
      and tableone.temp_int2 = #{user.tempInt2,jdbcType=INTEGER}
    
    
      and  tableone.temp_int3 = #{user.tempInt3,jdbcType=INTEGER}
